def count_long_words(s):
    words = s.split()
    count = 0
    for word in words:
        if len(word) > 10:
            count += 1
    return count

def fireworks(x=None, *args, **kwargs):
    if x is None:
        x = []

    a = 0
    b = 0

    for i in x:
        a += i
        b += i**2

    ans = a**2 - b + len(x) * kwargs.get("b", 0)
    return ans

